import dash
from dash import html
import dash_design_kit as ddk
import plotly.graph_objects as go

# app = dash.Dash(__name__)

app = dash.Dash(
    __name__,
    assets_folder='assets',  # This is default, shown for clarity
    serve_locally=True,
    external_scripts=[],     # make sure it doesn't pull plotly.js from CDN
)

# Create simple line chart data
fig = go.Figure()
fig.add_trace(go.Scatter(
    x=[1, 2, 3],
    y=[2, 6, 3],
    mode='lines+markers',
    name='Sample Data'
))
fig.update_layout(
    title='Simple Line Chart',
    xaxis_title='X Values',
    yaxis_title='Y Values'
)

app.layout = ddk.App([
    ddk.Card(ddk.Graph(figure=fig))
], show_editor=True)

if __name__ == '__main__':
    app.run(debug=True)